import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Container, Dropdown, Form, Row } from "react-bootstrap";
import Product from "./Product";

export default function Main(props: any) {
    const { products, onAdd } = props;
    const [searchTerm, setSearchTerm] = useState("");

    return (
        <div>
            <Container fluid>
                <div id="search">
                    <Form.Control
                        onChange={event => {
                            setSearchTerm(event.target.value)
                        }}
                        type="text"

                    />
                </div>
                <Row>
                    {products.filter((product: { name: string; }) => {
                        if (searchTerm === "") {
                            return product
                        } else if (product.name.toLowerCase().startsWith(searchTerm.toLowerCase())) {
                            return product
                        }
                    }).map((product: { id: React.Key | null | undefined; }) => (
                        <Product key={product.id} product={product} onAdd={onAdd} />
                    ))}
                </Row>
            </Container>
        </div>
    )
}
