import React, { ReactNode } from "react";

export default function Basket(props: any) {
    const {cartItems, onAdd, onRemove} = props;
    return (
        <aside>
            <h2>Cart Items</h2>
            <div>
                {cartItems.length === 0 && <div>Cart Is Empty</div>}
            </div>
            {cartItems.map((item: {
                qty: ReactNode;
                price: any; id: React.Key | null | undefined; name: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | React.ReactFragment | React.ReactPortal | null | undefined; 
}) =>(
                <div key={item.id} className='row'>
                    <div className="col-2">{item.name}</div>
                    <div className="col-2">
                        <button onClick={()=>onAdd(item)} className="add">+</button>
                        <button onClick={()=>onRemove(item)} className="remove">-</button>
                    </div>
                    <div className="col-2 text-right">
                        {item.qty} x {item.price ? item.price.toFixed(2) : 1}
                    </div>
                </div>
            ))}
        </aside>
    )
}
