import React, { ReactNode } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";

export default function Basket(props: any) {
    const { cartItems, onAdd, onRemove } = props;
    const itemsPrice = cartItems.reduce((a: number, c: { price: number; qty: number; }) => a + c.price * c.qty, 0)
    return (
        <Container fluid>
            <h2>Cart</h2>
            <div>
                {cartItems.length === 0 && <div>Cart Is Empty</div>}
            </div>
            {cartItems.map((item: {
                qty: number;
                price: number;
                id: number;
                name: string;
            }) => (
                <Container key={item.id}>
                    <Row>
                    <Col>
                    {item.name}

                    </Col>
                    {item.qty} x {item.price ? item.price.toFixed(2) : 0}
                    <Col>
                    </Col>
                    <Col>
                    <Button onClick={() => onAdd(item)} variant="success">+</Button>
                    <Button onClick={() => onRemove(item)} variant="danger">-</Button>
                    </Col>
                    </Row>

                </Container>
            ))}
            {cartItems.length !== 0 && (
                <>
                    <hr></hr>
                    <div>Items Price</div>
                    <div>{itemsPrice.toFixed(2)}â‚¬</div>
                </>
            )}
        </Container>
    )
}
