import React from 'react'
import { Accordion, Col, Figure } from 'react-bootstrap'
import Button from 'react-bootstrap/esm/Button'
import Card from 'react-bootstrap/esm/Card'

export default function Product(props: any) {
  const { product, onAdd } = props

  const filteredData = props.filter((el: any) => {
    if (props.input === "") {
      return el;
    }
    else {
      return el.text.toLowerCase().includes(props.input)
    }
  })

  return (
    <Col>
        <Card style={{ width: '18rem' }}>
          <Card.Img src={product.image ? product.image : "https://via.placeholder.com/286x286.png?text=Image+Not+Found"} alt={product.name} />
          <Card.Body>
            <Card.Title>{product.name}</Card.Title>
            <Card.Text>
              {product.price ? (product.price + "€") : "-"}
            </Card.Text>
            <div className="d-grid gap-2">
              <Button variant="primary" onClick={() => onAdd(product)}>Add to cart</Button>
            </div>
            <Accordion>
              <Accordion.Item eventKey="0">
                <Accordion.Header>Read More</Accordion.Header>
                <Accordion.Body>
                  <div>Description: {product.description}</div>
                  <div>Rating: {product.rating}</div>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </Card.Body>
        </Card>
      </Col>
  )
}
