import React from "react";

export default function Basket(props: any) {
    const {cartItems, onAdd} = props;
    return (
        <aside className="block .col-1">
            <h2>Cart Items</h2>
            <div>
                {cartItems.length === 0 && <div>Cart Is Empty</div>}
            </div>
            {cartItems.map((item: { id: React.Key | null | undefined; name: string | number | boolean | React.ReactElement<any, string | React.JSXElementConstructor<any>> | React.ReactFragment | React.ReactPortal | null | undefined; }) =>(
                <div key={item.id} className='row'>
                    <div>{item.name}</div>
                    <div>
                        <button onClick={()=>onAdd(item)} className="add">+</button>
                        <button onClick={()=>onAdd(item)} className="remove">-</button>
                    </div>
                </div>
            ))}
        </aside>
    )
}
