import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Dropdown, Row } from "react-bootstrap";
import Product from "./Product";

export default function Main(props: any) {
    const { products, onAdd } = props;
    return (
        <div>
            <Container fluid>
                <h1>Products</h1>
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Sort By
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        <Dropdown.Item href="#/action-1" onClikc="sort()">Price ascending</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">Price descending</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Name ascending</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Name descending</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
                <Row>
                    {products.map((product: { id: any }) => (
                        <Product key={product.id} product={product} onAdd={onAdd} />
                    ))}
                </Row>
            </Container>
        </div>
    )
}
